/**
 * CAT组件的主体
 */
(function (w) {

    //创建catUI对象
    var catUI = w.catUI || {};
    //建立catUI版本
    catUI.version = '1.0.0';
    //组件注册对象
    var component = {};
    /**
     * 注册组件
     * 只有被注册后的组件才可以通过use方法使用
     * 注册的组件必须拥有 name 与 init属性
     * 其中name属性为组件的名称，用于对组件的调用
     * 其中init为组件方法的执行入口
     */
    catUI.register = function (comp) {
        if (!comp.name) throw "The component must hava its name.";
        if (typeof comp.init != 'function') throw "The component must hava its init function.";
        component[comp.name] = comp.init;
    }

    /**
     * 使用组件
     */
    catUI.use = function (name, option) {
        if (!name || !component[name]) throw "This component does not exist";
        var _rObj = component[name](option);
        if (_rObj) {
            return _rObj;
        }
    }
    //返回catUI
    w.catUI = catUI;
})(window);